func SolutionExists(ctx context.Context, exec boil.ContextExecutor, iD int) (bool, error) {
	var exists bool
	sql := "select exists(select 1 from \"solutions\" where \"id\"=$1 limit 1)"

	if boil.IsDebug(ctx) {
		writer := boil.DebugWriterFrom(ctx)
		fmt.Fprintln(writer, sql)
		fmt.Fprintln(writer, iD)
	}
	row := exec.QueryRowContext(ctx, sql, iD)

	err := row.Scan(&exists)
	if err != nil {
		return false, errors.Wrap(err, "models: unable to check if solutions exists")
	}

	return exists, nil
}

// Exists checks if the Solution row exists.
func (o *Solution) Exists(ctx context.Context, exec boil.ContextExecutor) (bool, error) {
	return SolutionExists(ctx, exec, o.ID)
}
